mapper.select:
	SELECT FAB_ID,
	       AREA_ID,
	       EQP_GRP,
	       EQP_ID,
	       ALARM_ID,
	       SNRO_ID,
	       JOB_ID,
	       JOB_STATUS
	       START_TM,
	       END_TM		   
	  FROM MAS_JOBRUNNING_INF
   	 WHERE FAB_ID = #{FAB_ID}
	   AND AREA_ID = #{AREA_ID}
	   AND EQP_GRP = #{EQP_GRP}
	   AND EQP_ID = #{EQP_ID}
	   AND ALARM_ID = #{ALARM_ID}
	   AND SNRO_ID = #{SNRO_ID}
